async function loadCards(){const t=await fetch("data/discover.json"),e=await t.json(),n=document.getElementById("discoverGrid");e.forEach(((t,e)=>{const a=document.createElement("section");a.classList.add("card"),a.style.gridArea=`card${e+1}`,a.innerHTML=`\n        <h2>${t.title}</h2>\n        <figure>\n          <img src="${t.image}" alt="${t.alt}" loading="lazy">\n        </figure>\n        <address>${t.address}</address>\n        <p>${t.description}</p>\n        <button onclick="window.open('${t.url}', '_blank')">Learn More</button>\n      `,n.appendChild(a)}))}function displayVisitMessage(){const t=document.getElementById("visitorMessage"),e=Date.now(),n=localStorage.getItem("lastVisit");let a="Welcome! Let us know if you have any questions.";if(n){const t=Math.floor((e-Number(n))/864e5);a=0===t?"Back so soon! Awesome!":1===t?"You last visited 1 day ago.":`You last visited ${t} days ago.`}localStorage.setItem("lastVisit",e),t.textContent=a}async function loadEvents(){try{const t=await fetch("data/event.json"),e=await t.json(),n=document.getElementById("events-container");e.events.forEach((t=>{const e=document.createElement("div");e.classList.add("event-card"),e.innerHTML=`\n          <h3>${t.title}</h3>\n          <p><strong>Date:</strong> ${new Date(t.date).toLocaleDateString()}</p>\n          <address><strong>Location:</strong> ${t.location}</address>\n          <p>${t.description}</p>\n          <button>${t.cta}</button>\n        `,n.appendChild(e)}))}catch(t){console.error("Error loading events:",t)}}document.addEventListener("DOMContentLoaded",(()=>{loadCards(),displayVisitMessage(),document.getElementById("year").textContent=(new Date).getFullYear()})),document.addEventListener("DOMContentLoaded",loadEvents);